import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            helpr
            {/* <i class='fab fa-typo3' /> */}

          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Services
              </Link>
            </li>
            <li class=""><a href="subscriptions" class="dropdwn">Subscriptions</a>
    	      <ul>
            <li class=""><a href="homespa" class="clearfix"><div class="hs_cir nav_cir"></div><span>Home Spa</span></a></li>
            <li class=""><a href="homeguard" class="clearfix"><div class="hg_cir nav_cir"></div><span>Home Guard</span></a></li>
            </ul>
            </li>
            <li className='nav-item'>
              <Link
                to='/products'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                About
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/services'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Contact
              </Link>
            </li>

            <li>
              <Link
                to='/sign-up'
                className='nav-links-mobile'
                onClick={closeMobileMenu}
              >
                Get the App
              </Link>
            </li>
          </ul>
          {button && <Button buttonStyle='btn--outline'>Get the App</Button>}
        </div>
      </nav>
    </>
  );
}

export default Navbar;
