import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from 'react';
//import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Dropdown } from 'bootstrap';

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
    <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            helpr 
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            {/* <i className={click ? 'fas fa-times' : 'fas fa-bars'} /> */}
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
          <li class=""><a href="cleaning" class="dropdwn">Services</a>
            {/* <li className='nav-item' class="dropdown"> */}
            <ul>
      <li class=""><a href="cleaning" class="clearfix"><div class="cln_cir nav_cir"></div><span>Cleaning</span></a></li>
      <li class=""><a href="pestcontrol" class="clearfix"><div class="pest_cir nav_cir"></div><span>Pest Control</span></a></li>
       <li class=""><a href="courierservice" class="clearfix"><div class="computer_cir nav_cir"></div><span>Courier Services</span></a></li>
       <li class=""><a href="relocation" class="clearfix"><div class="vehicle_cir nav_cir"></div><span>Relocation</span></a></li>

    </ul>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                {/* Services */}
              </Link>
            </li>
            <li className='nav-item'>
              <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
                Subscription
              </Link>
            </li>
            <li className='nav-item'>
              <Link to='/products' className='nav-links' onClick={closeMobileMenu}>
                About
              </Link>
            </li>
            <li className='nav-item'>
              <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
                Contact
              </Link>
            </li>
            <li>
              <Link to='/sign-up' className='nav-links-mobile' onClick={closeMobileMenu}>
                Get the App
              </Link>
            </li>
          </ul>
          <button type="button" class="btn btn-primary">Get the App</button>
          {/* {button && <Button buttonStyle='btn--outline'>Get the App</Button>} */}
        </div>
      </nav>
    </>
  );
}

export default Navbar;
